	vTaskDelay(10);
	bt816_cmd_inflate(MEM_LAMP1, lamp1, sizeof(lamp1));
	vTaskDelay(10);
	bt816_cmd_inflate(MEM_LAMP2, lamp2, sizeof(lamp2));
